{
        "type": "AdaptiveCard",
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.2",
    "backgroundImage": {
        "url": "@{variables('colourBlue')}"
      },
    "body": [
        {
            "type": "ColumnSet",
            "columns": [
                {
                    "type": "Column",
                    "width": "stretch",
                    "items": [
                      {
                        "type": "TextBlock",
                            "text": "@{variables('bodySplitArr')[0]}",
                            "wrap": true,
                            "horizontalAlignment": "Left",
                            "size": "Large",
                            "weight": "Bolder"
                        },
                        {
                            "type": "TextBlock",
                            "text": "Error code:",
                             "size": "Large",
                             "spacing": "default",
                            "weight": "Bolder",
                            "horizontalAlignment": "Left"               
                        },
                        {
                            "type": "TextBlock",
                            "text": "@{variables('bodySplitArr')[1]}",
                             "horizontalAlignment": "Left",
                            "size": "Medium",
                             "spacing": "None",
                             "Weight": "italic",
                            "isSubtle": true,
                             "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "Error description:",
                             "size": "Large",
                             "spacing": "default",
                             "weight": "Bolder",
                            "horizontalAlignment": "Left"
                        },
                        {
                            "type": "TextBlock",
                            "text": "@{variables('bodySplitArr')[2]}",
                            "horizontalAlignment": "Left",
                            "size": "Small",
                             "spacing": "None",
                            "isSubtle": true,
                             "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "Error resolution:",
                            "spacing": "default",
                            "size": "Large",
                            "weight": "Bolder",
                            "horizontalAlignment": "Left"
                        },
                        {
                            "type": "TextBlock",
                            "text": "@{variables('bodySplitArr')[3]}",
                            "horizontalAlignment": "Left",
                             "spacing": "None",
                            "size": "Small",
                            "isSubtle": true,
                             "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "@{variables('bodySplitArr')[4]}",
                            "wrap": true,
                            "spacing": "default",
                            "size": "Large",
                            "weight": "Bolder",
                            "horizontalAlignment": "Left"
                        }
                    ],
                    "width": "auto"
                }
            ]
        },
        {
            "type": "ColumnSet",
            "columns": [
                {
                    "type": "Column",
                    "width": "stretch",
                    "items": [
                        {
                            "type": "Image",
                            "url": "@{outputs('imgURI')}",
                            "horizontalAlignment": "Center"
                        }
                    ]
                }
            ]
        }
    ]
}

